import React, {useState,useEffect} from "react";
import axios from "./Request/axios";
import "./Banner.css"


const img_url="https://image.tmdb.org/t/p/original/"

function Banner(props){

    const[poster,setPoster] = useState([]);

    useEffect(()=>{
        async function getPoster(){
            const response = axios.get(props.request)
            // console.log(response)
            let n = Math.floor(Math.random()*((await response).data.results.length)-1)
            // console.log(n)
            setPoster((await response).data.results[n])
        }
        getPoster();
    },[])

    function truncate(data){
        return data?data.substr(0,100)+"...":"No data"
    }

    return(
        <div className="banner_container"
        style={{
            backgroundSize:"cover",
            backgroundImage:`url(${img_url}${poster.backdrop_path})`,
            backgroundPosition:"center center",
            objectFit:"contain",
            height:"100%",
        }}>
            <h1>{poster.name}</h1>
            <button>Play</button>
            <button>My List</button>
            <p>{truncate(poster.overview)}</p>
            {/* <p>{typeof(poster.overview)}</p> */}
        </div>
    )
}

export default Banner;
